#include <iostream>
#include <cstdlib>
#include <string>

using namespace std;

int getFloorNumber(string input) {
  int floorNumber = 0;

  for (int  i = 0; i < input.length(); i++) {
    if (input[i] == '(') {
      floorNumber += 1;
    } else if (input[i] == ')') {
      floorNumber -= 1;
    }

    if (floorNumber == -1) {
      cout << i + 1 << endl;
    }
  }
  return floorNumber;
}

